<?phpecho "Фото удалены <br>";$dir = "/var/www/rigla.ru/upload/storage/goods/";function fileIsDir($search, $dir){  if(!opendir($dir)){    echo "Каталог не найден";    return false;  }    $files = scandir($dir);  foreach($files as $file){  	if(in_array($file, array('.','..'))) continue;          if($search == $file)        return true;    }      return false;  }$arr = explode(',',$_POST["images"]);$i = 0;foreach($arr as $elem){      if($elem){        $filename = $dir.$elem.".jpg";        if(fileIsDir($elem.".jpg", $dir)){      if(!unlink($filename))                echo "Ошибка удаления файла".$elem." <br>";          echo "Удаление изображения ".$elem.".jpg прошло успешно <br>";        $i++;    }    else      echo "Файл ".$elem." не найден <br>";  }    }echo "Всего удалено ".$i.".jpg изображений"; ?>